import React, {useEffect, useState} from 'react'
import './Apis.css'
import redarrow from './TradExpress/img/redarrow.png'
import greenarrow from './TradExpress/img/greenarrow.png'

const Apis = () => {
    const [data, setData] = useState({})
    const [fetching, setFetching] = useState(false)
    const [btc, setBtc] = useState('')
    const [eth, setEth] = useState('')
    const [usdt, setUsdt] = useState('')
    const [usdc, setUsdc] = useState('')
    const [bnb, setBnb] = useState('')


    useEffect(() => {
      setTimeout(() => {

        const Chart = async() => {
            let respond = await fetch('https://api.coinlore.net/api/tickers/?start=0&limit=5')
            let jsonData =await respond.json()
            setData(jsonData.data)
            setFetching(true)
            console.log(jsonData.data[1]);
            setBtc(jsonData.data[0])
            setEth(jsonData.data[1])
            setUsdt(jsonData.data[2])
            setUsdc(jsonData.data[3])
            setBnb(jsonData.data[4])

          } 



        Chart()
      }, 1000);

    }, [])

  return (
    <div className='apis'>
      <div>
      <h1>{btc.name}</h1>
      <p>{btc.percent_change_1h}</p>
      <img src={redarrow} alt="" />
      <h2>{btc.price_usd}</h2>
      </div>
      <div>
      <h1>{eth.name}</h1>
      <p>{eth.percent_change_1h}</p>
      <h2>{eth.price_usd}</h2>
      <img src={greenarrow} alt="" />
      </div>
      <div>
      <h1>{usdt.name}</h1>
      <p>{usdt.percent_change_1h}</p>
      <img src={greenarrow} alt="" />
      <h2>{usdt.price_usd}</h2>
      </div>
      <div>
      <h1>{usdc.name}</h1>
      <p>{usdc.percent_change_1h}</p>
      <img src={redarrow} alt="" />
      <h2>{usdc.price_usd}</h2>
      </div>
      <div>
      <h1>{bnb.name}</h1>
      <p>{bnb.percent_change_1h}</p>
      <h2>{bnb.price_usd}</h2>
      </div>
    </div>
  )
}

export default Apis
