import './Apis.css'
import redarrow from './TradExpress/img/redarrow.png'
import greenarrow from './TradExpress/img/greenarrow.png'

const Apis = () => {
    const [data, setData] = useState({})
    const [fetching, setFetching] = useState(false)
    const [btc, setBtc] = useState('')
    const [eth, setEth] = useState('')
    const [usdt, setUsdt] = useState('')
    const [usdc, setUsdc] = useState('')
    const [bnb, setBnb] = useState('')


    useEffect(() => {
      setTimeout(() => {

        const Chart = async() => {
            let respond = await fetch('https://api.coinlore.net/api/tickers/?start=0&limit=5')
            let jsonData =await respond.json()
            setData(jsonData.data)
            setFetching(true)
            console.log(jsonData.data[1]);
            setBtc(jsonData.data[0])
            setEth(jsonData.data[1])
            setUsdt(jsonData.data[2])
            setUsdc(jsonData.data[3])
            setBnb(jsonData.data[4])

          } 



        Chart()
      }, 1000);

    }, [])

  return (
    <div>
    {fetching && data.map((datum) => {
      const { id, name, percent_change_1h, price_usd } = datum
     return (
       <div key={id} className='api'>
        <h1>{}</h1>
       </div>
     )
    })}
    </div>
  )
}

export default Apis
